frappe.ui.form.on('Landed Cost Voucher', {
    onload: function(frm) {
      if(cur_frm.doc.__islocal == 1 ){
        var taxes = [
                {'desc':'التأمين'},
                {'desc':'مصاريف الأعتماد'},
                {'desc':'مصاريف التفتيش يورو'},
                {'desc':'مصاريف الشحن'},
                {'desc':'مصاريف الجمرك'},
                {'desc':'نقل دخلي'},
                {'desc':'مناولة'},
                {'desc':'غرمات تأخير'},
                {'desc':'مصاريف أخرى'},
            ];
        $.each(taxes, function(index, row){
                       var d = cur_frm.add_child("taxes");
                        d.description =row.desc;
                        cur_frm.refresh_field("taxes");

        });
      };
    } 
});




frappe.ui.form.on("Landed Cost Taxes and Charges", "rate", function(frm, cdt, cdn) { 
    var item = locals[cdt][cdn]; 
    var factor = item.factor;
    var rate = item.rate;
    var result = rate*factor;
    item.amount = result; 
    cur_frm.refresh_field("taxes");

});



frappe.ui.form.on("Landed Cost Taxes and Charges", "factor", function(frm, cdt, cdn) { 
    var item = locals[cdt][cdn]; 
